import com.eviware.soapui.support.XmlHolder

def addReq=testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["GetEmp"].getPropertyValue("Request")

//def id=testRunner.testCase.testSuite.testCases["GetEmployee"].getPropertyValue("id")
//def dept=testRunner.testCase.testSuite.testCases["GetEmployee"].getPropertyValue("department")
def xmlAdd=new XmlHolder(addReq)


xmlAdd.setNodeValue("//typ:getEmployeeDetails/typ:employeeName","gauri")

def newAddXML=xmlAdd.getXml();

testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["GetEmp"].setPropertyValue("Request",newAddXML)


import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

def tsAdd=testRunner.testCase.testSuite.testCases["AddEmployee"].testSteps["AddEmp"]
def contextAdd=new WsdlTestRunContext(tsAdd)
tsAdd.run(testRunner,contextAdd)


def vName=testRunner.testCase.testSuite.testCases["AddEmployee"].getPropertyValue("name")
def vResponse=testRunner.testCase.testSuite.testCases["GetEmployee"].testSteps["GetEmp"].getPropertyValue("Response")

def vGetRes=new XmlHolder(vResponse)
def vNameRes=vGetRes.getNodeValue("//ns:name")
assert vNameRes==vName

log.info vNameRes
log.info vName
